#include <cassert>
#include <algorithm>
#include <vector>
#include <iostream>

using namespace std;

int main() {
    unsigned short T;
    scanf("%hd", &T); // NOLINT(cert-err34-c)
    assert(1 <= T && T <= 10);
    while (T--) {
        unsigned int N, M;
        scanf("%d", &N);  // NOLINT(cert-err34-c)
        assert(1 <= N && N <= 1000000);
        vector<unsigned int long> Nt;
        while (N--) {
            unsigned int n;
            scanf("%d", &n);  // NOLINT(cert-err34-c)
            assert(0 <= n && n <= 0xffffffff);
            Nt.push_back(n);
        }
        sort(Nt.begin(), Nt.end());

        scanf("%d", &M);  // NOLINT(cert-err34-c)
        assert(1 <= M && M <= 1000000);

        while (M--) {
            unsigned long b, e;
            unsigned long long as_count = 0;

            scanf("%ld %ld", &b, &e);  // NOLINT(cert-err34-c)
            assert(0 <= b && b <= e);

            auto b_in_Nt = lower_bound(Nt.cbegin(), Nt.cend(), b);
            auto e_in_Nt = upper_bound(Nt.cbegin(), Nt.cend(), e);

            as_count = e_in_Nt == Nt.end() ? distance(b_in_Nt, Nt.cbegin() + Nt.size()) : e_in_Nt - b_in_Nt;
            cout << as_count << endl;
        }
    }
    return 0;
}

